SQLResource.query:
INSERT INTO COMMON
          ( CODE_TP
          , CODE_CD
          , CODE_NM
          , BRAND_CD
          , SORT_SEQ
          , ACC_CD
          , USE_YN
          , INST_DT
          , INST_USER
          , UPD_DT
          , UPD_USER                        
           )
        VALUES
        (   ${CODE_TP}
          , ${CODE_CD}
          , ${CODE_NM}
          , ${BRAND_CD}
          , ${SORT_SEQ}
          , ${ACC_CD}
          , ${USE_YN}
          , SYSDATE
          , ${INST_USER}
          , SYSDATE
          , ${UPD_USER}   
        )

UPDATE COMMON 
   SET CODE_NM      = ${CODE_NM}
    , BRAND_CD    = ${BRAND_CD}
    , ACC_CD      = ${ACC_CD}
    , SORT_SEQ   = ${SORT_SEQ}
    , USE_YN      = ${USE_YN}
    , UPD_DT      = SYSDATE
    , UPD_USER    = ${UPD_USER}
 WHERE CODE_TP = ${CODE_TP}
   AND CODE_CD = ${CODE_CD}

INSERT INTO LANG_COMMON
         (  CODE_TP
          , CODE_CD
          , LANGUAGE_TP
          , CODE_NM
          , USE_YN
          , INST_DT
          , INST_USER
          , UPD_DT
          , UPD_USER                        
           )
        VALUES
        (   ${CODE_TP}
          , ${CODE_CD}
          , ${LANG_TP}
          , ${CODE_NM}
          , ${USE_YN}
          , SYSDATE
          , ${INST_USER}
          , SYSDATE
          , ${UPD_USER}   
        )

UPDATE LANG_COMMON 
   SET CODE_NM      = ${CODE_NM}
     , USE_YN      = ${USE_YN}
     , UPD_DT      = SYSDATE
     , UPD_USER    = ${UPD_USER}
 WHERE CODE_TP     = ${CODE_TP}
   AND CODE_CD     = ${CODE_CD}
   AND LANGUAGE_TP = ${LANG_TP}
