      , FAX          = ${FAX}
      , E_MAIL       = ${E_MAIL}
      , DV_USER_ID   = ${DV_USER_ID}
      , SV_USER_ID   = ${SV_USER_ID}
      , CENTER_CD    = ${CENTER_CD}
      , SIDO_CD      = ${SIDO_CD}
      , REGION_CD    = ${REGION_CD}
      , TRAD_AREA    = ${TRAD_AREA}
      , APP_DIV      = NVL(${APP_DIV}, '01')
      , REST_AREA_TP = ${REST_AREA_TP}
      , CONTRACT_NM  = ${CONTRACT_NM}
      , EXECUTE_NM   = ${EXECUTE_NM}
      , CONTRACT_DIV = ${CONTRACT_DIV}
      , OPEN_DT      = REPLACE(${OPEN_DT}      , '-', '')
      , CLOSE_DT     = REPLACE(${CLOSE_DT}     , '-', '')
      , OFFER_TM     = ${OFFER_TM}
      , STOR_FORM    = ${STOR_FORM}
      , TABLE_NO     = ${TABLE_NO}
      , SEAT         = ${SEAT}
      , SPACE        = ${SPACE}
      , NATION_CD    = ${NATION_CD}
      , CURRENCY_CD  = ${CURRENCY_CD}
      , REGION_NO    = ${REGION_NO}
      , DSTN_COMP    = ${DSTN_COMP}
      , COMM_YN      = ${COMM_YN}
      , INSHOP_YN    = ${INSHOP_YN}
      , USE_YN       = ${USE_YN}
      , INST_DT      = SYSDATE
      , INST_USER    = ${UPD_USER}
      , UPD_DT       = SYSDATE
      , UPD_USER     = ${UPD_USER}
WHERE   COMP_CD      = ${COMP_CD}
AND     BRAND_CD     = ${BRAND_CD}
AND     STOR_CD      = ${STOR_CD}

  
MERGE   INTO LANG_STORE
USING   DUAL
ON  (
            OMP_CD     = ${COMP_CD}
       AND  BRAND_CD    = ${BRAND_CD}
       AND  STOR_CD     = ${STOR_CD}
       AND  LANGUAGE_TP = ${LANGUAGE_TP}
    )
 WHEN MATCHED THEN
    UPDATE  
       SET  STOR_NM     = ${STOR_NM}
         ,  USE_YN      = ${USE_YN}
         ,  UPD_DT      = SYSDATE
         ,  UPD_USER    = ${USER_ID}
 WHEN NOT MATCHED THEN
    INSERT   
    (          
            COMP_CD
         ,  BRAND_CD
         ,  STOR_CD
         ,  LANGUAGE_TP
         ,  STOR_NM
         ,  USE_YN
         ,  INST_DT
         ,  INST_USER  
         ,  UPD_DT
         ,  UPD_USER 
    ) VALUES (
            ${COMP_CD}
         ,  ${BRAND_CD}
         ,  ${STOR_CD}
         ,  ${LANGUAGE_TP}
         ,  ${STOR_NM}
         ,  ${USE_YN}
         ,  SYSDATE
         ,  ${UPD_USER}
         ,  SYSDATE
         ,  ${UPD_USER}
    )

MERGE  INTO STORE_WEEK
USING  DUAL
ON  (
            COMP_CD     = ${COMP_CD}
       AND  BRAND_CD    = ${BRAND_CD}
       AND  STOR_CD     = ${STOR_CD}
       AND  WEEK_DAY    = ${WEEK_DAY}
   )
WHEN MATCHED THEN
    UPDATE
       SET  START_HM    = ${START_HM}
         ,  CLOSE_HM    = ${CLOSE_HM}
         ,  UPD_DT      = SYSDATE
         ,  UPD_USER    = ${UPD_USER}
WHEN NOT MATCHED THEN
    INSERT 
    (  
            COMP_CD
         ,  BRAND_CD
         ,  STOR_CD
         ,  WEEK_DAY
         ,  START_HM
         ,  CLOSE_HM 
         ,  INST_DT
         ,  INST_USER
         ,  UPD_DT
         ,  UPD_USER  
    ) VALUES (
            ${COMP_CD}
         ,  ${BRAND_CD}
         ,  ${STOR_CD}
         ,  ${WEEK_DAY}
         ,  ${START_HM}
         ,  ${CLOSE_HM}
         ,  SYSDATE
         ,  ${UPD_USER}
         ,  SYSDATE
         ,  ${UPD_USER}
    )

MERGE  INTO STORE_FLAG
USING  DUAL
ON  (
            COMP_CD     = ${COMP_CD}
       AND  BRAND_CD    = ${BRAND_CD}
       AND  STOR_CD     = ${STOR_CD}
       AND  STOR_FG     = ${STOR_FG}
       AND  STOR_DT_CD  = ${STOR_DT_CD}
   )
WHEN MATCHED THEN
    UPDATE
       SET  USE_YN      = ${USE_YN}
         ,  UPD_DT      = SYSDATE
         ,  UPD_USER    = ${UPD_USER}
WHEN NOT MATCHED THEN
    INSERT 
    (  
            COMP_CD
         ,  BRAND_CD
         ,  STOR_CD
         ,  STOR_FG
         ,  STOR_DT_CD
         ,  USE_YN 
         ,  INST_DT
         ,  INST_USER
         ,  UPD_DT
         ,  UPD_USER  
    ) VALUES (
            ${COMP_CD}
         ,  ${BRAND_CD}
         ,  ${STOR_CD}
         ,  ${STOR_FG}
         ,  ${STOR_DT_CD}
         ,  ${USE_YN}
         ,  SYSDATE
         ,  ${UPD_USER}
         ,  SYSDATE
         ,  ${UPD_USER}
    )

MERGE  INTO STORE_POS_MST
USING  DUAL
ON  (
            COMP_CD     = ${COMP_CD}
       AND  BRAND_CD    = ${BRAND_CD}
       AND  STOR_CD     = ${STOR_CD}
       AND  POS_NO      = ${POS_NO}
   )
WHEN MATCHED THEN
    UPDATE
       SET  POS_NM      = ${POS_NM}
         ,  IP_ADDR     = ${IP_ADDR}
         ,  TRANS_TYPE  = ${TRANS_TYPE}
         ,  TOP_IMAGE_YN= ${TOP_IMAGE_YN}
         ,  USE_YN      = ${USE_YN}
         ,  UPD_DT      = SYSDATE
         ,  UPD_USER    = ${UPD_USER}
WHEN NOT MATCHED THEN
    INSERT 
   (  
        COMP_CD
      , BRAND_CD
      , STOR_CD
      , POS_NO
      , POS_NM
      , IP_ADDR
      ,  TRANS_TYPE
      , TOP_IMAGE_YN
      , USE_YN
      , INST_DT
      , INST_USER
      , UPD_DT
      , UPD_USER  
   ) 
    VALUES 
   (
    ${COMP_CD}
    ,  ${BRAND_CD}
    ,  ${STOR_CD}
    ,  ${POS_NO}
    ,  ${POS_NM}
    ,  ${IP_ADDR}
    ,  ${TRANS_TYPE}
    ,  ${TOP_IMAGE_YN}
    ,  ${USE_YN}
    ,  SYSDATE
    ,  ${UPD_USER}
    ,  SYSDATE
    ,  ${UPD_USER}
    )

SELECT  *
  FROM  STORE_JOIN
 WHERE  COMP_CD     = ${SCH_COMP_CD}
   AND  BRAND_CD    = ${SCH_BRAND_CD}
   AND  STOR_CD     = ${SCH_STOR_CD}
   AND  JOIN_COMP_CD= ${SCH_JOIN_COMP_CD}
   AND  (${SCH_USE_YN} IS NULL OR USE_YN = ${SCH_USE_YN})

MERGE  INTO STORE_JOIN
USING  DUAL
ON  (
            COMP_CD         = ${COMP_CD}
       AND  BRAND_CD        = ${BRAND_CD}
       AND  STOR_CD         = ${STOR_CD}
   )
WHEN MATCHED THEN
    UPDATE
       SET  JOIN_BRAND_CD   = ${JOIN_BRAND_CD}
         ,  JOIN_STOR_CD    = ${JOIN_STOR_CD}
         ,  JOIN_STOR_NM    = ${JOIN_STOR_NM}
         ,  JOIN_ITEM_CD    = ${JOIN_ITEM_CD}
         ,  JOIN_ITEM_NM    = ${JOIN_ITEM_NM}
         ,  USE_YN          = ${USE_YN}
         ,  UPD_DT          = SYSDATE
         ,  UPD_USER        = ${UPD_USER}
WHEN NOT MATCHED THEN
    INSERT 
    (  
            COMP_CD
         ,  BRAND_CD
         ,  STOR_CD
         ,  JOIN_COMP_CD
         ,  JOIN_BRAND_CD
         ,  JOIN_STOR_CD
         ,  JOIN_STOR_NM
         ,  JOIN_ITEM_CD
         ,  JOIN_ITEM_NM
         ,  USE_YN
         ,  INST_DT
         ,  INST_USER
         ,  UPD_DT
         ,  UPD_USER  
    ) VALUES (
            ${COMP_CD}
         ,  ${BRAND_CD}
         ,  ${STOR_CD}
         ,  ${JOIN_COMP_CD}
         ,  ${JOIN_BRAND_CD}
         ,  ${JOIN_STOR_CD}
         ,  ${JOIN_STOR_NM}
         ,  ${JOIN_ITEM_CD}
         ,  ${JOIN_ITEM_NM}
