SQLResource.query:
UPDATE  BUTTON_INFO 
SET     BTN_USE     = 'N'
     ,  UPD_DT      = SYSDATE
     ,  UPD_USER    = ${USER_ID}
 WHERE  COMP_CD     = ${SCH_COMP_CD}
   AND  BRAND_CD    = ${BRAND_CD}
   AND  STOR_CD     = ${STOR_CD}
   AND  POS_NO      = ${POS_NO}
   AND  STORE_GB    = ${STORE_GB}
   AND  BTN_CD      = ${BTN_CD}

MERGE INTO BUTTON_INFO
USING DUAL
ON  (
             COMP_CD     =  ${SCH_COMP_CD}
         AND BRAND_CD    =  ${BRAND_CD}
         AND STOR_CD     =  ${STOR_CD}
         AND POS_NO      =  ${POS_NO}
         AND STORE_GB    =  ${STORE_GB}
         AND BTN_GRP_CD  =  ${BTN_GRP_CD}
         AND BTN_SEQ     =  ${BTN_SEQ}
    )
WHEN  MATCHED  THEN
   UPDATE
      SET    BTN_PG_NM   =  ${BTN_PG_NM}
        ,    BTN_CD      =  ${BTN_CD}
        ,    BTN_TEXT    =  ${BTN_TEXT}
        ,    BTN_EVENT   =  ${BTN_EVENT}
        ,    BTN_FCOLOR  =  ${BTN_FCOLOR}
        ,    BTN_ECOLOR  =  ${BTN_ECOLOR}
        ,    FONT_COLOR  =  ${FONT_COLOR}
        ,    BTN_USE     =  ${BTN_USE}
        ,    UPD_DT      =  SYSDATE
        ,    UPD_USER    =  ${USER_ID}
WHEN NOT MATCHED THEN
   INSERT (   
               COMP_CD
           ,   BRAND_CD
           ,   STOR_CD
           ,   POS_NO 
           ,   STORE_GB
           ,   BTN_GRP_CD
           ,   BTN_SEQ
           ,   BTN_GRP_NM
           ,   BTN_PG_NM
           ,   BTN_CD
           ,   BTN_TEXT
           ,   BTN_EVENT
           ,   BTN_FCOLOR
           ,   BTN_ECOLOR
           ,   FONT_COLOR
           ,   BTN_USE
           ,   INST_DT
           ,   INST_USER 
           ,   UPD_DT 
           ,   UPD_USER
          ) VALUES (
               ${SCH_COMP_CD}
           ,   ${BRAND_CD}
           ,   ${STOR_CD}
           ,   ${POS_NO}
           ,   ${STORE_GB}
           ,   ${BTN_GRP_CD}
           ,   ${BTN_SEQ}
           ,   ${BTN_GRP_NM}
           ,   ${BTN_PG_NM}
           ,   ${BTN_CD}
           ,   ${BTN_TEXT}
           ,   ${BTN_EVENT}
           ,   ${BTN_FCOLOR}
           ,   ${BTN_ECOLOR}
           ,   ${FONT_COLOR}
           ,   ${BTN_USE}
           ,   SYSDATE
           ,   ${USER_ID}
           ,   SYSDATE
           ,   ${USER_ID}
          )

DELETE  BUTTON_INFO
 WHERE  COMP_CD     = ${SCH_COMP_CD}
   AND  BRAND_CD    = ${BRAND_CD}
   AND  STOR_CD     = ${STOR_CD}
   AND  POS_NO      = ${POS_NO}
   AND  STORE_GB    = ${STORE_GB}
