SQLResource.query:
MERGE   INTO ITEM_CLASS IC
USING   DUAL D
ON  ( 
            IC.COMP_CD      = ${COMP_CD} 
       AND  IC.ORG_CLASS_CD = ${ORG_CLASS_CD}
       AND  IC.ITEM_CD      = ${ITEM_CD}
    )
WHEN MATCHED  THEN
    UPDATE
       SET  L_CLASS_CD      = ${L_CLASS_CD}
         ,  M_CLASS_CD      = ${M_CLASS_CD}
         ,  S_CLASS_CD      = ${S_CLASS_CD}
         ,  D_CLASS_CD      = ${D_CLASS_CD}
         ,  USE_YN          = ${USE_YN}
         ,  UPD_DT          = SYSDATE
         ,  UPD_USER        = ${UPD_USER}
WHEN NOT MATCHED THEN
    INSERT 
    (
            COMP_CD
         ,  ORG_CLASS_CD
         ,  ITEM_CD
         ,  L_CLASS_CD
         ,  M_CLASS_CD
         ,  S_CLASS_CD
         ,  D_CLASS_CD
         ,  USE_YN 
         ,  INST_DT 
         ,  INST_USER 
         ,  UPD_DT 
         ,  UPD_USER
    ) VALUES (
            ${COMP_CD}
         ,  ${ORG_CLASS_CD}
         ,  ${ITEM_CD}
         ,  ${L_CLASS_CD}
         ,  ${M_CLASS_CD}
         ,  ${S_CLASS_CD}
         ,  ${D_CLASS_CD}
         ,  ${USE_YN}
         ,  SYSDATE
         ,  ${UPD_USER}
         ,  SYSDATE
         ,  ${UPD_USER}
    )
