SQLResource.query:
MERGE   INTO CS_CONTENT
USING   DUAL
ON (
            COMP_CD         = ${COMP_CD}
       AND  CONTENT_SEQ     = ${CONTENT_SEQ}
   )
WHEN MATCHED THEN
    UPDATE
       SET  SUBJECT         = ${SUBJECT}
         ,  CONTENT         = TRIM(REPLACE(REPLACE(${CONTENT}, CHR(13), ''), CHR(10), '@$'))
         ,  CONTENT_DIV1    = ${CONTENT_DIV1}
         ,  CONTENT_DIV2    = ${CONTENT_DIV2}
         ,  TEMPLETE_ID     = ${TEMPLETE_ID}
         ,  REPLACE_CONTENT = TRIM(REPLACE(REPLACE(${REPLACE_CONTENT}, CHR(13), ''), CHR(10), '@$'))
         ,  SEND_POINT      = ${SEND_POINT}
         ,  MBS_DIV         = ${MBS_DIV}
         ,  BRAND_CD        = ${BRAND_CD}
         ,  USE_YN          = ${USE_YN}
         ,  UPD_DT          = SYSDATE
         ,  UPD_USER        = ${UPD_USER}
WHEN NOT MATCHED THEN
    INSERT
    (
            COMP_CD
         ,  CONTENT_SEQ
         ,  SUBJECT
         ,  CONTENT
         ,  CONTENT_FG
         ,  CONTENT_DIV1
         ,  CONTENT_DIV2
         ,  TEMPLETE_ID
         ,  REPLACE_CONTENT
         ,  SEND_POINT
         ,  MBS_DIV
         ,  BRAND_CD
         ,  USE_YN
         ,  INST_DT
         ,  INST_USER
         ,  UPD_DT
         ,  UPD_USER
    )VALUES(
            ${COMP_CD}
         ,  (
                SELECT  NVL(MAX(TO_NUMBER(CONTENT_SEQ)),0) + 1  AS CONTENT_SEQ
                  FROM  CS_CONTENT
                 WHERE  COMP_CD         = ${COMP_CD}
            )
         ,  ${SUBJECT}
         ,  TRIM(REPLACE(REPLACE(${CONTENT}, CHR(13), ''), CHR(10), '@$'))
         ,  ${CONTENT_FG}
         ,  ${CONTENT_DIV1}
         ,  ${CONTENT_DIV2}
         ,  ${TEMPLETE_ID}
         ,  TRIM(REPLACE(REPLACE(${REPLACE_CONTENT}, CHR(13), ''), CHR(10), '@$'))
         ,  ${SEND_POINT}
         ,  ${MBS_DIV}
         ,  ${BRAND_CD}
         ,  ${USE_YN}
         ,  SYSDATE
         ,  ${UPD_USER}
         ,  SYSDATE
         ,  ${UPD_USER}
    )
